import React, {useState, useEffect} from 'react'
import ReactMapGL, {Marker, Popup} from 'react-map-gl';
import Plot from 'react-plotly.js';


const Map = ({stateObj, data, layout}) => {
    console.log(stateObj);
    const [viewport, setViewport] = useState({
        longitude:-97,
        latitude:40,
        width: "1000px",
        height: "1000px",
        zoom: 2.5
    })
    const [showInfo, setShowInfo] = useState(false)

    useEffect(() => {
        const listener = (e) => {
            if(e.key === "Escape"){
                setShowInfo(false)
            }
        }
        window.addEventListener("keydown", listener)
        return
    }, [])
    return (
        <>
            <ReactMapGL {...viewport} 
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN} 
            mapStyle='mapbox://styles/hunterhutch/ckwjv9etg27z614r7bdgamfwl'
            onViewportChange={viewport => {
                setViewport(viewport)
            }}
            >
                <Marker latitude={stateObj.coordinates.latitude} longitude={stateObj.coordinates.longitude}>
                    <button onClick={(e) => {
              e.preventDefault();
              setShowInfo(!showInfo)
            }}></button>
                </Marker>

                {showInfo 
                ? 
                <Popup latitude={stateObj.coordinates.latitude} 
                longitude={stateObj.coordinates.longitude}
                onClose={()=>{
                    setShowInfo(false)
                }}
                >
                    <Plot data={data} layout={layout} />
                </Popup>
                :null}
            </ReactMapGL>
        </>
    )
}

export default Map
