import React, {useState} from 'react'
import {useSelector} from 'react-redux';
import Plot from 'react-plotly.js';


const SelectState = () => {
    const usData = useSelector(state => state.covidData.usData)
    const stateData = useSelector(state => state.covidData.stateData)
    const [selectedState, setSelectedState] = useState(null)
    const [pieData, setPieData] = useState([])
    const [pieLayout, setPieLayout] = useState({})
    const [barData, setBarData] = useState([])
    const [barLayout, setBarLayout] = useState({})

    const handleSelectState = (e) => {
        let state = stateData.find(({state})=> state === e.target.value)
        let dataForPie = [{
            values: [state.hospitalizedCurrently, state.death, (state.positive - state.hospitalizedCurrently - state.death)],
            labels: ['Active Cases', 'Deaths', 'Recovered'],
            domain: {column: 0},
            name: `${state.state} Covid Cases`,
            hoverinfo: 'label+value',
            hole: .4,
            type: 'pie'
        },
        {
            values: [(usData.positive-state.positive), state.positive],
            labels: ['Rest of the US', state.state],
            domain: {column: 1},
            name: `${state.state} vs 
Rest of the US`,
            hoverinfo: 'label+value',
            hole: .4,
            type: 'pie'
        }]
        let layoutForPie = {
            title: `${state.state} Covid Breakdown`,
            annotations: [
                {
                    font: {
                    size: 8
                    },
                    showarrow: false,
                    text: `${state.state} Covid Cases`,
                    x: 0.17,
                    y: 0.5
                },
                {
                    font: {
                    size: 8
                    },
                    showarrow: false,
                    text: `vs Rest of the US`,
                    x: 0.85,
                    y: 0.5
                }
            ],
            height: 400,
            width: 600,
            showlegend: false,
            grid: {rows: 1, columns: 2}
        };
        let dataForBar = [{
            x: [],
            y: [],
            type: 'bar'
        }]
        let layoutForBar = {
            width: 1000,
            height: 500,

        }
        setPieData(dataForPie)
        setPieLayout(layoutForPie)

        setSelectedState(state)
    }
    return (
        <>
        <select className="height38" defaultValue={selectedState} onChange={handleSelectState}>
          <option hidden value="defaultValue">Pick a State</option>
          {stateData.map(state => {
            return <option key={state.state} value={state.state}>{state.state}</option>
          })}
        </select>
        {selectedState 
        ? 
        <>
        <Plot data={pieData} layout={pieLayout} />
        <Plot data={pieData} layout={pieLayout} />
        </>
        : 
        null
        }




          <button onClick={()=>setSelectedState(null)}>Reset</button>

        </>
    )
}

export default SelectState
