import { LOAD_DATA } from "../actions/types"
import * as  stateCoordList from '../assets/data.json';


const initialState = {
    usData: {},
    stateData: []
}

const covidDataReducer = (state=initialState, action) => {
    console.log(stateCoordList.states)
    switch(action.type){
        case LOAD_DATA:
            let statelist = [...action.data.stateData]
            statelist.map((state, index)=>{
                if(state.state === stateCoordList.states[index].name){
                    return state.coordinates = stateCoordList[index]
                }
            })
            console.log(statelist)
            return {
                ...state,
                usData: action.data.usData,
                stateData: action.data.stateData
            }
        default:
            return state
    }
}

export default covidDataReducer
